$(document).ready(function(){
	AOS.init({ disable: 'mobile' });
});
window.onload = function() {
    $(window).resize(function(){
        fixHeight();
    });
    fixHeight();
  var comma,
      operator,
      number,
      output = document.getElementById("display");
  var btnsNumber = document.querySelectorAll(".number");
  var btnsOperator = document.querySelectorAll(".operator");

  for(var i = 0; i < btnsNumber.length; i++ ) {
    btnsNumber[i].addEventListener("click",function() {
      number = this.value;
      if(output.innerHTML == "0") output.innerHTML = number;
      else output.innerHTML += number;
    },false);
  }

  for(var i = 0; i < btnsOperator.length; i++ ) {
      btnsOperator[i].addEventListener("click",function() {
      operator = this.value;
      if(output.innerHTML === "") output.innerHTML = output.innerHTML.concat("");
      else output.innerHTML = output.innerHTML.concat(operator);
    },false);
  }

  document.querySelector(".comma").addEventListener("click",function() {
    comma = this.value;
    if(output.innerHTML === "") output.innerHTML = output.innerHTML.concat("0.");
    else if(!output.innerHTML.split(/[^A-Za-z0-9]/)[output.innerHTML.split(/[^A-Za-z0-9]/).length-1].includes(".") && output.innerHTML.split(/[^A-Za-z0-9.]/)[output.innerHTML.split(/[^A-Za-z0-9.]/).length-1].split(".").length-1 < 1) output.innerHTML = output.innerHTML.concat(".");
  },false);

  document.querySelector(".eq").addEventListener("click",function() {
    if(eval(output.innerHTML) == "Infinity") alert("ERROR! DIV/0 MAYBE?");
    else output.innerHTML = eval(output.innerHTML);
  },false);  

  document.querySelector(".ac").addEventListener("click",function() {
    output.innerHTML = "0";
  },false);

  document.querySelector(".ce").addEventListener("click", function() {
    output.innerHTML = output.innerHTML.substring(0, output.innerHTML.length - 1);
    if(output.innerHTML == "") output.innerHTML = "0";
  }, false);
}

let fixHeight = function(){
    $(".btn").css("height", $(".btn")[0].offsetWidth);
};
